    $entered_otp = $_POST['otp'];
    $stored_otp = $_SESSION['otp'];
    
 
 
    if($entered_otp == $stored_otp) {
        // echo "<script>alert('OTP Verification Successful.');</script>";
             echo "<script>window.open('forget_password.php?email=$email','_self')</script>";
